/* 
    Light green: #7ed56f
    Medium Green: #55c57a
    Dark green: #28b485
*/

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}
body {
    font-family: "Lato", sans-serif;
    font-weight: 400;
    font-size:16px;
    line-height: 1.7;
    color: #777;
    padding:30px;
}
.header {
    height:95vh;
    background: linear-gradient(to right bottom, rgba(126,213,111,0.8),rgba(40,180,131,0.8);
    background-size: cover;
    background-position: center;
     /* top left x, y. || top right x, y, || bottom left x,y || bottom right x,y*/
    clip-path: polygon(0 0 ,100% 0, 100% 75vh, 0 100%);
    border-radius:0.9%;
    /* header, parent element is a place ,
    the origin of where to start */
    position: relative;
    /* topleft topright bottomleft bottomright */
}

.logo-box {
    position: absolute;
    /* from the top 40 */
    /* a place from where we start */
    top:40px;
    /* from the left 40*/
    left:40px;
}

.logo {
    height:35px;
}

.text-box {
    position: absolute;
    top:50%;
    left:50%;
    transform:translate(-50%,-50%);
    /* 
        inline-block
        is looked as text.
     */
    text-align:center;
    /* background-color: red; */
}

.heading-primary {
    color:#fff;
    text-transform: uppercase;
    margin-bottom:60px;
    backface-visibility: hidden;
    /* determines if the back part
    of an element when you transform is visible
    or hidden for the user or rotate 100element
    it would be logical to see 100%, 
    the back part, the borders?
     */
}

.heading-primary-main {
    display:block;
    font-size: 60px;
    font-weight: 400;
    /* h1 has a higher by default */
    letter-spacing: 35px;

    animation-name: moveInLeft;
    animation-duration: 2s;
    animation-timing-function: ease-out;

}
.heading-primary-sub {
    display:block;
    font-size:20px;
    font-weight:400;
    letter-spacing: 17.4px;
    animation: moveInRight 1s ease-out;
}
@keyframes moveInLeft {
    0% {
        opacity: 0;
        transform:translateX(-100px);
    }

    80% {   
        transform: translateX(10px);
    }   

    100% {
        opacity: 1;
        transform: translate(0);
    }
}
@keyframes moveInRight {
    0% {
        opacity: 0;
        transform:translateX(100px);
    }

    100% {
        opacity: 1;
        transform: translate(0);
    }
}
.btn:link,
.btn:visited {
    text-transform: uppercase;
    text-decoration: none;
    padding:15px 40px;
    /* padding with height  */
    display:inline-block;
    /* treated as text */
    /* pseudo special states of a selector
    check box is clicked,
    last child,
    styling on a special condition,
    when it is an anchor element
     */
     border-radius:100px;
     transition: all .2s;
     position: relative;

}
.btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 10px 20px rgba(0,0,0.2);
    /* offset in X direction
        Shadow one
        Blur: 
        color
    */
}
.btn:active {
    transform: translateY(-1px);
    box-shadow: 0 5px 10px rgba(0,0,0.2);

}
.btn-white {
    background-color:#fff;
    color:#777;
}
.btn::after {
    content: '';
    display: inline-block;
    height:100%;
    width:100%;
    border-radius:100px;
    position: absolute;
    top: 0;
    left: 0;
    z-index:-1;
    /*  positioning
        if there one on top of another. 
     */
}
.btn-white::after{
    background-color:red;
}
.btn:hover::after {
    transform: scale(2);
}
